import { useState } from "react";
import searchIcon from "../images/search-icon.svg";
import foalder from "../images/foalder.svg";
import allModels from "../images/all-models.svg";

function Header() {

  const [focus, setFocus] = useState("my");

    return (
      <header className="header">
        <div className="header__container">
          <div className="inputs-container inputs-container_type_header">
            <input
              className={`input`}
              type="text"
              placeholder="Поиск"
              name="search"
            />
            <img src={searchIcon} className="input__image" alt="Значок поиска"/>
          </div>
        <button className="button header__button"></button>
        </div>
        
        <nav className="navigation">
          <button className={`navigation__button ${focus === "my" && `navigation__button_active`}`}>
            <img className="navigation__button-image" src={foalder} alt="Иконка папки"/>
            { focus === "my" && `Мои модели` }
          </button>
          <button className={`navigation__button ${focus === "all" && `navigation__button_active`}`}>
            <img className="navigation__button-image" src={foalder} alt="Иконка папки"/>
            { focus === "all" && `Все модели` }
          </button>
          {/* <button>Все модели</button>
          <button>Понравившиеся</button> */}
        </nav>
        
      </header>
    );
  }
  
  export default Header;
