import { useState } from "react";
import searchIcon from "../images/search-icon.svg";
import foalder from "../images/foalder.svg";
import allModels from "../images/all-models.svg";
import likedModels from "../images/liked-models.svg";
import buttonBackground from "../images/nav-button.png";

function Header() {

  const [focus, setFocus] = useState("liked");

    return (
      <header className="header">
        <div className="header__container">
          <div className="inputs-container inputs-container_type_header">
            <input
              className={`input`}
              type="text"
              placeholder="Поиск"
              name="search"
            />
            <img src={searchIcon} className="input__image" alt="Значок поиска"/>
          </div>
        <button className="button header__button"></button>
        </div>
        
        <nav className="navigation">
          <button className={`navigation__button ${focus === "my" && `navigation__button_active`}`}>
            <img className="navigation__button-image" src={foalder} alt="Иконка папки"/>
            { focus === "my" && `Мои модели` }
          </button>
          <button className={`navigation__button ${focus === "all" && `navigation__button_active`}`}>
            <img className="navigation__button-image" src={allModels} alt="Иконка глобуса"/>
            { focus === "all" && `Все модели` }
          </button>
          <button className={`navigation__button ${focus === "liked" && `navigation__button_active`}`}>
            <img className="navigation__button-image" src={likedModels} alt="Иконка лайка"/>
            { focus === "liked" && `Понравившиеся` }
          </button>
          <img src={buttonBackground} style={ (focus === "my" && {left: 0 + 'px'}) || (focus === "liked" && {left: 157 + 'px', transition: 'all 0.5s linear'})} className="navigation__button-background" alt="Обои кнопки"/>
        </nav>
        
      </header>
    );
  }
  
  export default Header;
