import React from "react";
import { useState, useEffect } from "react";
import editButton from "../images/edit-button.svg";
import addButton from "../images/add-button.svg";
import api from "../api/Api";
import Card from "./Card";

function Main(onEditProfile, handleAddPlaceClick, onEditAvatar) {
  const [userName, setUserName] = useState("");
  const [userAbout, setUserAbout] = useState("");
  const [userAvatar, setUserAvatar] = useState("");
  const [cards, setCards] = useState([]);

  useEffect(() => {
    Promise.all([api.getInitialInfo(), api.getInitialCards()])
      .then(([userData, cardsData]) => {
        setUserName(userData.name);
        setUserAbout(userData.about);
        setUserAvatar(userData.avatar);
        setCards(cardsData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <main className="content">
      <section className="profile">
        <figure className="profile__container">
          <button
            className="button profile__avatar-button"
            onClick={onEditAvatar}
          >
            <img
              className="profile__edit-button-image profile__edit-button-image_type_avatar"
              src={editButton}
              alt="Кнопка изменить аватар"
            />
          </button>
          <img
            src={userAvatar}
            className="profile__avatar"
            alt="Аватарка профиля пользователя"
          />
          <figcaption className="profile__info">
            <div className="profile__info-container">
              <h1 className="profile__title">{userName}</h1>
              <p className="profile__subtitle">{userAbout}</p>
            </div>
            <button
              type="button"
              className="button profile__edit-button"
              onClick={onEditProfile}
            >
              <img
                className="profile__edit-button-image"
                src={editButton}
                alt="Кнопка изменить профиль"
              />
            </button>
          </figcaption>
        </figure>
        <button type="button" className="button profile__add-button">
          <img
            className="profile__add-button-image"
            src={addButton}
            alt="Кнопка плюс"
          />
        </button>
      </section>
      <section className="elements">
        {cards.map((card) => {
          return <Card key={card._id} prop={card} />;
        })}
      </section>
    </main>
  );
}

export default Main;
