import React, { useState, useEffect, useRef } from "react";
import errorIcon from "../images/error-icon.svg";
import nameModelIcon from "../images/name-model.svg";
import glbIcon from "../images/glb-file.svg";

function PopupAddModel({
  isOpen,
  setOpen,
  onValidation,
  isError,
  messageError,
}) {
  const formRef = useRef();
  const inputRef = useRef(null);

  const [isValidity, setValidity] = useState(true);

  const [inputs, setInputs] = useState({});

  useEffect(() => {
    setValidity(formRef.current.checkValidity());
  }, [isOpen, isError]);

  function handleChangeInput(e) {
    setInputs({
      ...inputs,
      [e.target.name]: e.target.value,
    });
    onValidation(e);
  }

  const handleClickUploadFile = () => {
    inputRef.current.click();
  };

  return (
    <div className={`popup ${isOpen && "popup_active"}`}>
      <div className="popup__container">
        <div>
          <button
            className="back-button"
            onClick={() => {
              setOpen(false);
            }}
          ></button>
          <h2 className="popup__title">Добавить модель</h2>
          <form
            className={`popup__form`}
            action="#"
            name={`popup-form-add-model`}
            ref={formRef}
            noValidate
          >
            <div
              className="inputs-container inputs-container_type_file"
              onClick={handleClickUploadFile}
            >
              <input
                className={`input input_type_file ${
                  isError.login && "input_type_error"
                }`}
                type="file"
                placeholder="Файл модели"
                name="login"
                minLength="3"
                ref={inputRef}
                accept=".glb"
                required
              />
              <div>
                <p className="inputs-container__text">Файл модели</p>
                <img
                  className="input__image input__image_type_file"
                  src={glbIcon}
                  alt="Иконка логина"
                />
              </div>
            </div>
            <div className="inputs-container">
              <input
                className={`input ${isError.login && "input_type_error"}`}
                type="text"
                placeholder="Название модели"
                name="login"
                minLength="3"
                onChange={handleChangeInput}
                required
              />
              {isError.login && (
                <img
                  className="input__error-image"
                  src={errorIcon}
                  alt="Иконка ошибки"
                />
              )}
              <img
                className="input__image"
                src={nameModelIcon}
                alt="Иконка логина"
              />
              <span
                className={`auth__input-error ${
                  isError.login && "auth__input-error_active"
                }`}
              >
                {isError.login && messageError.login}
              </span>
            </div>
            <button type="submit" className={`button popup__submit-button`}>
              Опубликовать
            </button>
          </form>
          <button type="button" className={`button popup__logout-button`}>
            Отменить
          </button>
        </div>
      </div>
    </div>
  );
}

export default PopupAddModel;
