import { useState, useEffect } from "react";
import { Route, Routes, useNavigate } from "react-router-dom";
import Wellcome from "./Wellcome";
import * as Auth from "../utils/Auth";

import AuthForm from "./AuthForm";
import Main from "./Main";

function App() {

  const [isError, setError] = useState({});
  const [messageError, setMessageError] = useState({});

  const [isLoggedIn, setLoggedIn] = useState(false);
  const [isAuth, setAuth] = useState(false);

  const [cards, setCards] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {

    if (isLoggedIn) {
      Promise.all([api.getInitialInfo(), api.getInitialCards()])

      .then(([userData, cardsData]) => {
        setCurrentUser(userData);
        setCards(cardsData.reverse());
      })

      .catch((error) => {
        console.log(error);
      });
    }
  }, [isLoggedIn]);

  function validateInputs(e) {
    if (!e.target.validity.valid) {
      setError({
        ...isError,
        [e.target.name]: true,
      });
      setMessageError({
        ...messageError,
        [e.target.name]: e.target.validationMessage,
      });
    } else {
      setError({
        ...isError,
        [e.target.name]: false,
      });
      setMessageError({
        ...messageError,
        [e.target.name]: "",
      });
    }
  }

  function handleRegister(inputs) {
    Auth.register(inputs.email, inputs.login, inputs.password)
      .then(() => {
        setAuth(true);
        setTimeout(()=> {
          navigate("/sign-in", { replace: true });
         }, 2000);
      })
      .catch((error) => {
        setAuth(false);
        console.log(error);
      })
  }

  function handleAuthorize(inputs) {
    Auth.authorize(inputs.login, inputs.password)
      .then((res) => {
        if (res.token) {
          localStorage.setItem("jwt", res.token);
          setLoggedIn(true);
          navigate("/models", { replace: true });
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }

    return (
        <div>
        <Routes>
        <Route
          path="/"
          element={
            <Wellcome />
            }
        />
        <Route
          path="/sign-up"
          element={
            <AuthForm 
              onValidation={validateInputs}
              isError={isError}
              messageError={messageError}
              title={"Создать аккаунт"}
              button={"Создать аккаунт"}
              paragraph={"Уже есть аккаунт"}
              link={"Войти"}
              isAuth={isAuth}
              onSubmit={handleRegister}
              register="true"
              route={"/sign-in"}
            />
            }
        />
        <Route
          path="/sign-in"
          element={
            <AuthForm 
              onValidation={validateInputs}
              isError={isError}
              messageError={messageError}
              title={"Вход"}
              button={"Войти"}
              paragraph={"Нет аккаунта?"}
              link={"Зарегистрироваться"}
              isAuth={isAuth}
              onSubmit={handleAuthorize}
              register={false}
              route={"/sign-up"}
            />
            }
        />
        <Route
          path="/models"
          element={
            <Main
            />
            }
        />
        </Routes>
      </div>
        
    );
  }
  
  export default App;
