import React, { useRef, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Auth({ onValidation, isError, onSubmit, title, button }) {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const formRef = useRef();
  const [isValidity, setValidity] = useState(true);

  useEffect(() => {
    setValidity(formRef.current.checkValidity());
  }, [isError]);

  function handleChangeEmail(e) {
    setEmail(e.target.value);
    onValidation(e);
  }

  function handleChangePassword(e) {
    setPassword(e.target.value);
    onValidation(e);
  }

  return (
    <div className="auth">
      <h1 className="auth__title">{title}</h1>
      <form
        className="auth__form"
        ref={formRef}
        name={`auth-form`}
        onSubmit={onSubmit}
        noValidate
      >
        <input
          className={`auth__input`}
          type="email"
          placeholder="Email"
          name="email"
          onChange={handleChangeEmail}
          required
        />
        <span
          className={`auth__input-error ${
            isError.email && "auth__input-error_active"
          }`}
        >
          {isError.email && messageError.email}
        </span>
        <input
          className={`auth__input`}
          type="password"
          placeholder="Пароль"
          name="password"
          onChange={handleChangePassword}
          required
        />
        <span
          className={`auth__input-error ${
            isError.password && "auth__input-error_active"
          }`}
        >
          {isError.password && messageError.password}
        </span>
        <button
          disabled={!isValidity}
          type="submit"
          className={`button auth__submit-button ${
            !isValidity && "button_disabled"
          }`}
        >
          {button}
        </button>
      </form>
    </div>
  );
}

export default Auth;
