import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import eye from "../images/eye.svg";
import eyeHiden from "../images/eye-hiden.svg";

function AuthForm({title, button}) {
  const [isVisible, setVisible] = useState(false);

  const navigate = useNavigate();

  function handleClickEye() {
    setVisible(!isVisible);
  }

  return (
    <div className="auth">
      <button className="back-button" onClick={() => navigate("/")}></button>
      <h1 className="auth__title">{title}</h1>
      <form
        className="auth__form"
        name={`auth-form`}
      >
        <input
          className={`auth__input`}
          type="email"
          placeholder="Эл. почта"
          name="email"
          required
        />
        <input
          className={`auth__input`}
          type="text"
          placeholder="Логин"
          name="login"
          minLength="3"
          required
        />
        <div className="inputs-container">
          <input
            className={`auth__input`}
            type="password"
            placeholder="Пароль"
            name="password"
            minLength="3"
            required
          />
          <img onClick={handleClickEye} className="auth__input-image" src={eye} alt="показать пароль"/>
        </div>
        
        <input
          className={`auth__input`}
          type="password"
          placeholder="Повторите пароль"
          name="password"
          minLength="3"
          required
        />
        <button
          type="submit"
          className={`button wellcome__button`}
        >
          {button}
        </button>
      </form>
      <p className="auth__text">Уже есть аккаунт? <Link to="/sign-in" className="auth__link">Войти</Link></p>
    </div>
  );
}

export default AuthForm;
