import React, { useRef , useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import eye from "../images/eye.svg";
import eyeHiden from "../images/eye-hiden.svg";
import loginIcon from "../images/login.svg";
import emailIcon from "../images/email.svg";
import errorIcon from "../images/error-icon.svg";

function AuthForm({ onValidation, isError, messageError, title, button}) {
  const [isVisible, setVisible] = useState(false);

  const formRef = useRef();
  const [isValidity, setValidity] = useState(true);

  useEffect(() => {
    setValidity(formRef.current.checkValidity());
  }, [isError]);

  const navigate = useNavigate();

  function handleChangeInput(e) {
    onValidation(e);
  }

  function handleClickEye() {
    setVisible(!isVisible);
  }

  return (
    <div className="auth">
      <button className="back-button" onClick={() => navigate("/")}></button>
      <h1 className="auth__title">{title}</h1>
      <form
        className="auth__form"
        ref={formRef}
        name={`auth-form`}
        noValidate
      > 
        <div className="inputs-container">
          <input
            className={`auth__input ${isError.email && "auth__input_type_error"}`}
            type="email"
            placeholder="Эл. почта"
            name="email"
            onChange={handleChangeInput}
            required
          />
          {isError.email && <img className="auth__input-error-image" src={errorIcon} alt="Иконка логина"/>}
          <img className="auth__input-image" src={emailIcon} alt="Иконка логина"/>
        </div>

        <span
          className={`auth__input-error ${
            isError.email && "auth__input-error_active"
          }`}
        >
          {isError.email && messageError.email}
        </span>

        <div className="inputs-container">
          <input
            className={`auth__input`}
            type="text"
            placeholder="Логин"
            name="login"
            minLength="3"
            required
          />
          <img className="auth__input-image" src={loginIcon} alt="Иконка логина"/>
        </div>

        <div className="inputs-container">
          <input
            className={`auth__input`}
            type={isVisible ? "text" : "password"}
            placeholder="Пароль"
            name="password"
            minLength="3"
            required
          />
          <img onClick={handleClickEye} className="auth__input-image" src={ isVisible ? eye : eyeHiden} alt={isVisible ? "Пароль показан" : "Пароль скрыт"}/>
        </div>
        
        <div className="inputs-container">
          <input
            className={`auth__input`}
            type={isVisible ? "text" : "password"}
            placeholder="Повторите пароль"
            name="repeat-password"
            minLength="3"
            required
          />
          <img onClick={handleClickEye} className="auth__input-image" src={ isVisible ? eye : eyeHiden} alt={isVisible ? "Пароль показан" : "Пароль скрыт"}/>
        </div>
        <button
          disabled={!isValidity}
          type="submit"
          className={`button wellcome__button ${
            !isValidity && "button_disabled"
          }`}
        >
          {button}
        </button>
      </form>
      <p className="auth__text">Уже есть аккаунт? <Link to="/sign-in" className="auth__link">Войти</Link></p>
    </div>
  );
}

export default AuthForm;
